package org.mit.engg.sy;

public class ExplicitDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=50;
		byte b=(byte)a;
		float f= 45.5f;
		short s =(short)f;
		System.out.println("value of byte: "+b);
		System.out.println("value of short: "+s);
	}

}
